/**
 * ✉️ Email Service Generator
 * 
 * Generate email services:
 * - SMTP, templates, queuing
 */

import { EventEmitter } from 'events';

export class EmailServiceGenerator extends EventEmitter {
    private static instance: EmailServiceGenerator;

    private constructor() { super(); }

    static getInstance(): EmailServiceGenerator {
        if (!EmailServiceGenerator.instance) {
            EmailServiceGenerator.instance = new EmailServiceGenerator();
        }
        return EmailServiceGenerator.instance;
    }

    generate(): string {
        return `// Email Service
// Generated by Shadow AI

import nodemailer from 'nodemailer';
import { render } from '@react-email/render';
import { Queue } from 'bullmq';

// Transporter
const transporter = nodemailer.createTransport({
    host: process.env.SMTP_HOST,
    port: parseInt(process.env.SMTP_PORT || '587'),
    secure: process.env.SMTP_SECURE === 'true',
    auth: {
        user: process.env.SMTP_USER,
        pass: process.env.SMTP_PASS
    }
});

// Email queue (optional, for async sending)
const emailQueue = new Queue('emails', { connection: { host: 'localhost', port: 6379 } });

// Templates
interface EmailTemplate<T = any> {
    subject: string;
    html: (data: T) => string;
}

const templates: Record<string, EmailTemplate> = {
    welcome: {
        subject: 'Welcome to Our Platform!',
        html: (data: { name: string }) => \`
            <!DOCTYPE html>
            <html>
            <head>
                <style>
                    body { font-family: Arial, sans-serif; line-height: 1.6; color: #333; }
                    .container { max-width: 600px; margin: 0 auto; padding: 20px; }
                    .header { background: #667eea; color: white; padding: 20px; text-align: center; }
                    .content { padding: 20px; }
                    .button { display: inline-block; padding: 12px 24px; background: #667eea; color: white; text-decoration: none; border-radius: 6px; }
                    .footer { text-align: center; padding: 20px; color: #666; font-size: 12px; }
                </style>
            </head>
            <body>
                <div class="container">
                    <div class="header">
                        <h1>Welcome!</h1>
                    </div>
                    <div class="content">
                        <p>Hi \${data.name},</p>
                        <p>Welcome to our platform! We're excited to have you on board.</p>
                        <p style="text-align: center;">
                            <a href="https://example.com/dashboard" class="button">Get Started</a>
                        </p>
                    </div>
                    <div class="footer">
                        <p>&copy; 2024 Our Company. All rights reserved.</p>
                    </div>
                </div>
            </body>
            </html>
        \`
    },
    passwordReset: {
        subject: 'Reset Your Password',
        html: (data: { name: string; resetUrl: string }) => \`
            <!DOCTYPE html>
            <html>
            <body style="font-family: Arial, sans-serif; padding: 20px;">
                <h2>Password Reset</h2>
                <p>Hi \${data.name},</p>
                <p>You requested a password reset. Click the button below:</p>
                <p>
                    <a href="\${data.resetUrl}" style="display: inline-block; padding: 12px 24px; background: #667eea; color: white; text-decoration: none; border-radius: 6px;">
                        Reset Password
                    </a>
                </p>
                <p>This link expires in 1 hour.</p>
                <p>If you didn't request this, please ignore this email.</p>
            </body>
            </html>
        \`
    },
    orderConfirmation: {
        subject: 'Order Confirmation #{{orderId}}',
        html: (data: { name: string; orderId: string; items: any[]; total: number }) => \`
            <!DOCTYPE html>
            <html>
            <body style="font-family: Arial, sans-serif; padding: 20px;">
                <h2>Order Confirmed!</h2>
                <p>Hi \${data.name},</p>
                <p>Your order #\${data.orderId} has been confirmed.</p>
                <table style="width: 100%; border-collapse: collapse;">
                    <tr style="background: #f5f5f5;">
                        <th style="padding: 10px; text-align: left;">Item</th>
                        <th style="padding: 10px; text-align: right;">Qty</th>
                        <th style="padding: 10px; text-align: right;">Price</th>
                    </tr>
                    \${data.items.map(item => \`
                        <tr>
                            <td style="padding: 10px;">\${item.name}</td>
                            <td style="padding: 10px; text-align: right;">\${item.quantity}</td>
                            <td style="padding: 10px; text-align: right;">$\${item.price.toFixed(2)}</td>
                        </tr>
                    \`).join('')}
                    <tr style="font-weight: bold;">
                        <td colspan="2" style="padding: 10px;">Total</td>
                        <td style="padding: 10px; text-align: right;">$\${data.total.toFixed(2)}</td>
                    </tr>
                </table>
            </body>
            </html>
        \`
    }
};

// Email Service
class EmailService {
    async send(options: {
        to: string | string[];
        template: keyof typeof templates;
        data: any;
        attachments?: any[];
    }) {
        const template = templates[options.template];
        if (!template) throw new Error(\`Template "\${options.template}" not found\`);

        const subject = template.subject.replace(/{{(\\w+)}}/g, (_, key) => options.data[key] || '');

        return transporter.sendMail({
            from: process.env.EMAIL_FROM || 'noreply@example.com',
            to: Array.isArray(options.to) ? options.to.join(', ') : options.to,
            subject,
            html: template.html(options.data),
            attachments: options.attachments
        });
    }

    async sendAsync(options: Parameters<typeof this.send>[0]) {
        await emailQueue.add('send', options, {
            attempts: 3,
            backoff: { type: 'exponential', delay: 1000 }
        });
    }

    async sendBatch(emails: Array<Parameters<typeof this.send>[0]>) {
        return Promise.all(emails.map(email => this.sendAsync(email)));
    }

    async verify() {
        return transporter.verify();
    }
}

export const emailService = new EmailService();
export { templates };
`;
    }
}

export const emailServiceGenerator = EmailServiceGenerator.getInstance();
